package main

import (
	"fmt"
)

func max(a, b int) int {
	if a < b {
		return b
	}
	return a
}

func longestCommonSubsequence(text1 string, text2 string) int {
	n1, n2 := len(text1), len(text2)

	lcs := make([][]int, 2)
	for i := 0; i < 2; i++ {
		lcs[i] = make([]int, n2 + 1)
	}

	prev, cur := lcs[1], lcs[0]
	for i := 1; i <= n1; i++ {
		prev, cur = cur, prev

		for j := 1; j <= n2; j++ {
			if text1[i - 1] == text2[j - 1] {
				cur[j] = prev[j - 1] + 1
			} else {
				cur[j] = max(prev[j], cur[j - 1])
			}
		}
	}

	return cur[n2]
}

func main() {
	s1 := ""
	s2 := ""
	res := longestCommonSubsequence(s1, s2)
	fmt.Println(res)
}
